import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBars } from '@fortawesome/free-solid-svg-icons';
import '../components/Navigation.css';

const Navigation = ({ isMenuOpen, handleMenuToggle, handleMenuItemClick }) => {
    const [menuOpen, setMenuOpen] = useState(isMenuOpen);

    const toggleMenu = () => {
        setMenuOpen((prevState) => !prevState);
        handleMenuToggle();
    };

    const handleClick = (sectionId) => {
        toggleMenu();
        handleMenuItemClick(sectionId);
    };

    return (
        <header className="header">
            <div className="container header-content">
                <h3>My Project</h3>
                <div className="menu-container">
                    <div className={`menu-icon ${menuOpen ? 'active' : ''}`} onClick={toggleMenu}>
                        <FontAwesomeIcon icon={faBars} />
                    </div>
                    <div className={`menu-items ${menuOpen ? 'open' : ''}`}>
                        <ul>
                            <li>
                                <a href="#employees-section" onClick={() => handleClick('employees-section')}>O nas</a>
                            </li>
                            <li>
                                <a href="#offer-section" onClick={() => handleClick('offer-section')}>Oferta</a>
                            </li>
                            <li>
                                <a href="#contact-section" onClick={() => handleClick('contact-section')}>Kontakt</a>
                            </li>
                        </ul>
                    </div>
                </div>
            </div>
        </header>
    );
};

export default Navigation;
