import React from 'react';
import '../style/EmployeeRow.css';

const EmployeeRow = ({ name, surname, position, description, photo, isLeftAligned }) => {
    const isRightAligned = !isLeftAligned; // Добавляем переменную для определения правого выравнивания
    return (
        <div className={`employee-row ${isLeftAligned ? 'left-align' : ''} ${isRightAligned ? 'right-align' : ''}`}>
            <div className="employee-details">
                <h3>{name} {surname}</h3>
                <p>{position}</p>
                <p>{description}</p>
            </div>
            <div className="employee-photo" style={{ backgroundImage: `url(${photo})` }}></div>
        </div>
    );
};

export default EmployeeRow;
