import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBars } from '@fortawesome/free-solid-svg-icons';
import OfferBox from './components/OfferBox';
import EmployeeRow from './components/EmployeeRow';
import offers from './data/offers';
import employees from './data/employees';
import './styles.css';

const App = () => {
    const [isMenuOpen, setMenuOpen] = React.useState(false);

    const toggleMenu = () => {
        setMenuOpen(!isMenuOpen);
    };

    const handleMenuItemClick = (sectionId) => {
        // Add logic to navigate to a new page or scroll to a specific section on the page based on the sectionId
        // For example, you can use React Router for navigation or use JavaScript scrollIntoView() method for scrolling
        console.log(`Clicked on section: ${sectionId}`);
    };

    return (
        <div className="app">
            <header className="header">
                <div className="header-content">
                    <h1>My Project</h1>
                    <div className={`menu-icon ${isMenuOpen ? 'active' : ''}`} onClick={toggleMenu}>
                        <FontAwesomeIcon icon={faBars} />
                    </div>
                    {isMenuOpen && (
                        <div className="menu-items">
                            <ul>
                                <li onClick={() => handleMenuItemClick('onas')}>O nas</li>
                                <li onClick={() => handleMenuItemClick('oferta')}>Oferta</li>
                                <li onClick={() => handleMenuItemClick('kontakt')}>Kontakt</li>
                            </ul>
                        </div>
                    )}
                </div>
            </header>

            <section id="onas" className="offers-section">
                <h2>Lista ofert</h2>
                {offers.map((offer) => (
                    <OfferBox key={offer.id} isNew={offer.isNew} title={offer.title} description={offer.description} />
                ))}
            </section>

            <section id="oferta" className="employees-section">
                <h2>Lista pracownik√≥w</h2>
                {employees.map((employee) => (
                    <EmployeeRow
                        key={employee.id}
                        name={employee.name}
                        surname={employee.surname}
                        position={employee.position}
                        description={employee.description}
                        photo={employee.photo}
                    />
                ))}
            </section>

            <footer className="footer">
                <div className="footer-content">
                    {/* Add footer content */}
                    <p>&copy; {new Date().getFullYear()} My Project. All rights reserved.</p>
                </div>
            </footer>
        </div>
    );
};

export default App;
