import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBars } from '@fortawesome/free-solid-svg-icons';
import '../style/Navigation.css';

const Navigation = ({ isMenuOpen, handleMenuItemClick }) => {
    const [menuOpen, setMenuOpen] = useState(isMenuOpen);
    const [activeSection, setActiveSection] = useState('');

    const toggleMenu = () => {
        setMenuOpen((prevState) => !prevState);
    };

    const handleSectionChange = (sectionId) => {
        setActiveSection(sectionId);
    };

    return (
        <header className="header">
            <div className="container header-content">
                <h3>My Project</h3>
                <div className="menu-container">
                    <div className={`menu-icon ${menuOpen ? 'active' : ''}`} onClick={toggleMenu}>
                        <FontAwesomeIcon icon={faBars} />
                    </div>
                    <div className={`menu-items ${menuOpen ? 'open' : ''}`}>
                        <ul>
                            <li>
                                <a
                                    href="#employees-section"
                                    className={activeSection === 'employees-section' ? 'active' : ''}
                                    onClick={() => handleMenuItemClick('employees-section')}
                                >
                                    Pracownicy
                                </a>
                            </li>
                            <li>
                                <a
                                    href="#offer-section"
                                    className={activeSection === 'offer-section' ? 'active' : ''}
                                    onClick={() => handleMenuItemClick('offer-section')}
                                >
                                    Oferta
                                </a>
                            </li>
                            <li>
                                <a
                                    href="#contact-section"
                                    className={`${activeSection === 'contact-section' ? 'active' : 'disabled-link'}`}
                                    onClick={() => handleMenuItemClick('contact-section')}
                                >
                                    Kontakt
                                </a>
                            </li>
                        </ul>
                    </div>
                </div>
            </div>
        </header>
    );
};

export default Navigation;
