import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBars } from '@fortawesome/free-solid-svg-icons';
import { useHistory } from 'react-router-dom'; // Подключение useHistory из react-router-dom
import '../style/Navigation.css';


const Navigation = ({ isMenuOpen, toggleMenu }) => {
    const history = useHistory(); // Использование useHistory для перенаправления пользователя

    const handleMenuItemClick = (section) => {
        if (section === 'about-section') {
            history.push('/o-nas'); // Перенаправление пользователя на '/o-nas'
        }
        // Добавьте обработку для других разделов при необходимости
    };

    return (
        <header className="header">
            <div className="container header-content">
                <h3>My Project</h3>
                <div className={`menu-icon ${isMenuOpen ? 'active' : ''}`} onClick={toggleMenu}>
                    <FontAwesomeIcon icon={faBars} />
                </div>
                {isMenuOpen && (
                    <div className="menu-items">
                        <ul>
                            <li onClick={() => handleMenuItemClick('about-section')}>
                                <button type="button">O nas</button>
                            </li>
                            <li onClick={() => handleMenuItemClick('offer-section')}>
                                <button type="button">Oferta</button>
                            </li>
                            <li style={{ cursor: 'not-allowed' }}>
                                <button type="button" disabled>Kontakt</button>
                            </li>
                        </ul>
                    </div>
                )}
            </div>
        </header>
    );
};

export default Navigation;
