import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBars } from '@fortawesome/free-solid-svg-icons';
import OfferBox from './components/OfferBox';
import EmployeeRow from './components/EmployeeRow';
import offers from './data/offers';
import employees from './data/employees';
import './styles.css';

const App = () => {
    const [isButtonHovered, setButtonHovered] = React.useState(false);
    const [isButtonClicked, setButtonClicked] = React.useState(false);
    const [isMenuOpen, setMenuOpen] = React.useState(false);

    const toggleMenu = () => {
        setMenuOpen(!isMenuOpen);
    };

    const handleButtonClick = () => {
        setButtonClicked(true);
        const offerSection = document.getElementById('offer-section');
        offerSection.scrollIntoView({ behavior: 'smooth' });
    };

    const handleMenuItemClick = (sectionId) => {
        const section = document.getElementById(sectionId);
        section.scrollIntoView({ behavior: 'smooth' });
    };

    return (
        <div className="container">
            <header className="header">
                <div className="header-content">
                    <h2>My Project</h2>
                    <div className={`menu-icon ${isMenuOpen ? 'active' : ''}`} onClick={toggleMenu}>
                        <FontAwesomeIcon icon={faBars} />
                    </div>
                    {isMenuOpen && (
                        <div className="menu-items">
                            <ul>
                                <li onClick={() => handleMenuItemClick('about-section')}>O nas</li>
                                <li onClick={() => handleMenuItemClick('offer-section')}>Oferta</li>
                                <li style={{ cursor: 'not-allowed' }}>Kontakt</li>
                            </ul>
                        </div>
                    )}
                </div>
            </header>

            <button
                className={`main-button ${isButtonHovered ? 'hovered' : ''} ${isButtonClicked ? 'clicked' : ''}`}
                onClick={handleButtonClick}
                onMouseEnter={() => setButtonHovered(true)}
                onMouseLeave={() => setButtonHovered(false)}
            >
                Przejdz do oferty
            </button>

            <section className="employees-section">
                <h2>Lista pracownikow</h2>
                {employees.map((employee, index) => (
                    <EmployeeRow
                        key={employee.id}
                        name={employee.name}
                        surname={employee.surname}
                        position={employee.position}
                        description={employee.description}
                        photo={employee.photo}
                        isRightAligned={index === 1} // Второй работник будет выровнен справа и ниже
                    />
                ))}
            </section>

            <section className="offers-section" id="offer-section">
                <h2>Lista ofert</h2>
                <div className="offer-grid">
                    {offers.map((offer) => (
                        <OfferBox key={offer.id} isNew={offer.isNew} title={offer.title} description={offer.description} />
                    ))}
                </div>
            </section>

            <footer className="footer">
                <div className="footer-content">
                    <p>&copy; {new Date().getFullYear()} My Project. All rights reserved.</p>
                </div>
            </footer>
        </div>
    );
};

export default App;
